import React from "react";
import { useNavigate } from "react-router-dom";
import styles from "./CstoreMain.module.css";
import { useState,useEffect } from "react";
import "./CstoreMain.module.css"
import Comic from "./Comic";
import Teens from "./Teens";
import Kodesh from "./Kodesh";
import Kids from "./"

const CStore = () => {
  const navigate = useNavigate();
 
  const [books,setBooks]=useState(new Array);
  const [flag,setFlag]=useState(true);
  const [element,setElement]=useState(null);
  async function showBook() {

    try {
      const response = await fetch("http://localhost:7002/api/books")
      const data = await response.json();
      setBooks(data)
      console.log(books);
    }
    catch (error) {
      console.log("failed to fetch");
    }
  }
  useEffect(() => { showBook() }, [])

  const filters = (page) => {
    setFlag(false)
    if(page==="comic")
    {
      var t =books.filter(e=>e.type===page)
      setElement(<Comic add={()=>addToCart()} t={t}></Comic>)
    }
    if(page==="teens")
    {
      var t =books.filter(e=>e.type===page)
      setElement(<Teens add={()=>addToCart()} t={t}></Teens>)
    }
  
   // navigate(`/${page}`,{state:{ob1:t,cart:addToCart}})
  }
  const addToCart=(b)=>{
    var myHeaders = new Headers();
    const obj = JSON.parse(sessionStorage.getItem("userToken"))
   const token = obj.accesstoken;
    console.log(token)
    myHeaders.append("Authorization", "Bearer "+token);
    myHeaders.append("Content-Type", "application/json");
    
    var raw = JSON.stringify({
      "book":b._id
    });
    
    var requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow'
    };
    var res
    fetch("http://localhost:7002/api/carts", requestOptions)
      .then(response => response.text())
      .then(result => {
        res=result
        console.log(result)})
      .catch(error => console.log('error', error));
      navigate("/Cart")
}
  return (
    
   flag?<div>
      <div className={styles.containerc}>
        <div className={styles.imagewrapperc}>
          <img
            src="gg.jpg"
            style={{ height: "180px", width: "180px", fontFamily: "initial" }}
            alt="Logo"
          />
        </div>
        <div className={styles.buttonwrapperc}>
          <button className={styles.buttonc} onClick={e=>{filters("teens")}}>
            Teens
          </button>
          <button className={styles.buttonc} onClick={r=>{filters("comic")}}>
            Comic
          </button>
          <button className={styles.buttonc} onClick={g=>{filters("kids")}}>
            Kids
          </button>
          <button className={styles.buttonc} onClick={g=>{filters("kodesh")}}>
            Kodesh
          </button>
        </div>
      </div>
    </div>
    :(element)
  
  );
};

export default CStore;
