import { useState,useEffect } from "react";

const Store = ()=>{

    const [books, setBooks] = useState([]);
     const [i, setA] = useState("");
    // const [b, setB] = useState("");
    // const [c, setC] = useState(0);
    // const [d, setD] = useState("");
    // const [p, setP] = useState("");
    async function showBook() {
        try {
          const response = await fetch("http://localhost:7002/api/books")
          const data = await response.json();
          setBooks(data)
          console.log(books);
        }
        catch (error) {
          console.log("failed to fetch");
        }
      }
      useEffect(() => { showBook() }, [])


    const deleteBook=(b)=>{

        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          code: b._id
        });
        
        var requestOptions = {
          method: 'DELETE',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        
        fetch("http://localhost:7002/api/books", requestOptions)
          .then(response => response.text())
          .then(result => {alert(result)
          setBooks(prevCartItems => prevCartItems.filter(item => item._id !== b._id));})
          .catch(error => console.log('error', error));
    }
    const updateBook=(i)=>{
      var a=books.findIndex(book => book.code === i.code);
      i=
      document.getElementById("dialog").showModal();




    }
    const update=async (i)=>{
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          name: i.name,
          code: i.code,
          price: i.price,
          author:i.author,
          image: i.image
        });
    
        var requestOptions = {
          method: 'PUT',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        fetch("http://localhost:7002/api/books", requestOptions)
        .then(response => response.text())
        .then(result => {
          console.log(result);
          setBooks(prevBooks => {
            const updatedBooks = [...prevBooks];
            const updatedIndex = updatedBooks.findIndex(book => book.code === i.code);
            updatedBooks[updatedIndex] = {
              ...updatedBooks[updatedIndex],
              name: i.name,
              code: i.code,
              price: i.price,
              author:i.author,
              image: i.image
            };
            return updatedBooks;
          });
        })
          .catch(error => console.log('error', error));
          document.getElementById("dialog").close()
          
        }  

          const cancel=()=>{
            document.getElementById("dialog").close()
          }

   
    return (
        <>
        <button onClick={()=>updateBook({})}>Add New Book</button>
        
           {books && books.map((i,index) => {
            return <div className='border' key={i._id}>
                    <img src={i.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
                   <div>{i.name + " | " + i.price + "₪"}</div>
                   <button  onClick={()=>updateBook(i)}>Edit</button>

                   <dialog id="dialog">
                    <h1>Add/Edit-Book</h1>
                    <input defaultValue={i.name} onBlur={e=>i.name=e.target.value}></input>
                    <br></br>
                    <input value={i.code}></input>
                    <br></br>
                    <input defaultValue={i.price}   onBlur={e=>i.price=e.target.value}></input>
                    <br></br>
                    <textarea defaultValue={i.author}  onBlur={e=>e=>i.author=e.target.value}></textarea>
                    <br></br>
                    <input defaultValue={i.image} onBlur={e=>e=>i.image=e.target.value}></input>
                    <br></br>
                    <img src={i.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
                    <br></br>
                    <button onClick={()=>update(i)}>save</button>
                    <button onClick={()=>cancel()}>cancel</button>
                   </dialog>
                   <button onClick={()=>deleteBook(i)}>Remove</button>        
            </div>})} 
           
        </>);
    

}
export default Store;
