import { useEffect,useState } from "react";
const Cart = () => {
  const obj = JSON.parse(sessionStorage.getItem("userToken"));
  const token = obj.accesstoken;
  console.log("1234567890-08765234" + token);
  var myHeaders = new Headers();
  myHeaders.append("Authorization", "Bearer " + token);

  var requestOptions = {
    method: 'GET',
    headers: myHeaders,
    redirect: 'follow',
  };

  const [cartItems, setCartItems] = useState([]); // New state to store the cart items

  useEffect(() => {
    fetch("http://localhost:7002/api/carts", requestOptions)
      .then(response => response.text())
      .then(result => {
        const parsedResult = JSON.parse(result); // Parse the result as JSON
        setCartItems(parsedResult); // Update the cart items state
        console.table(parsedResult);
      })
      .catch(error => console.log('error', error));
  }, []);

  return (
    <>
   
      {cartItems.map(b => (
    
        <div key={b._id}>
      
          <img src={b.book.image} style={{ height: "150px", width: "200px", fontFamily: "initial" }}></img>
          <br></br>
          {b.book.name + b.booprice + "₪"}
        </div>
      ))}
    </>
  );
};

export default Cart;
