import React, { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom"
import userName from "../Login/Login"
const CStore = ()=>{
  console.log()
  const navigate = useNavigate();
    const [books, setBooks] = useState([]);
    async function showBook() {
      try {
        const response = await fetch("http://localhost:7002/api/books")
        const data = await response.json();
        setBooks(data)
        console.log(books);
      }
      catch (error) {
        console.log("failed to fetch");
      }
    }
    useEffect(() => { showBook() }, [])
  
    const addToCart=(b)=>{

        var myHeaders = new Headers();
        const obj = JSON.parse(sessionStorage.getItem("userToken"))
       const token = obj.accesstoken;
        console.log(token)
        myHeaders.append("Authorization", "Bearer "+token);
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          "book":b._id
        });
        
        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        var res
        fetch("http://localhost:7002/api/carts", requestOptions)
          .then(response => response.text())
          .then(result => {
            res=result
            console.log(result)})
          .catch(error => console.log('error', error));
    }
    const myCart=async()=>{
      navigate("/Cart")

    }

    return(
        <>
        <h1>{"hello"+userName}</h1>
        <button onClick={() => myCart()}>my Cart</button>
        {books.map((b)=>
          <div key={b._id}>
                   
          <img src={b.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
          <br></br>
          {b.name + b.price+"â‚ª"}
  
          <button onClick={() => addToCart(b)}>AddðŸ›’</button>
          </div>  
       )} 

        </>
    );

}
export default CStore;
