import { useState,useEffect } from "react";



const CStore = ()=>{
    const [books, setBooks] = useState([]);
    async function showBook() {
      try {
        const response = await fetch("http://localhost:7002/api/books")
        const data = await response.json();
        setBooks(data)
        console.log(books);
      }
      catch (error) {
        console.log("failed to fetch");
      }
    }
    useEffect(() => { showBook() }, [])

    const addToCart=(b)=>{
        var myHeaders = new Headers();
        var a=sessionStorage.getItem("userToken")
        
        myHeaders.append("Authorization", sessionStorage.getItem("userToken"));
        console.log("session"+sessionStorage.getItem("userToken"))
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          "book":b._id
        });
        
        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        
        fetch("http://localhost:7002/api/carts", requestOptions)
          .then(response => response.text())
          .then(result => console.log(result))
          .catch(error => console.log('error', error));
    }
    
    return(
        <>
        {books.map((b)=>
          <div key={b._id}>
                   
          <img src={b.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
          <br></br>
          {b.name + b.price+"â‚ª"}
  
          <button onClick={() => addToCart(b)}>AddðŸ›’</button>
          </div>  
       )} 

        </>
    );

}
export default CStore;
