import React, { useState } from 'react';
import { InputText, Button, Password } from 'primereact/primeact';
import { ErrorMessage } from 'primereact/message/';

function Register({ onRegister }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('https://localhost:44393/api/Authentication/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password, confirmPassword, email }),
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error('Registration failed');
      }
      console.log(response);
      onRegister(); // Call the parent component function on successful registration
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div className="p-d-flex p-jc-center p-align-center">
      <form onSubmit={handleSubmit} className="p-form p-grid">
        <div className="p-field p-col-12 p-md-6">
          <label htmlFor="username">Username:</label>
          <InputText id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="p-field p-col-12 p-md-6">
          <label htmlFor="password">Password:</label>
          <Password id="password" value={password} onChange={(e) => setPassword(e.target.value)} feedback={confirmPassword !== password && 'Passwords do not match'} />
        </div>
        <div className="p-field p-col-12 p-md-6">
          <label htmlFor="confirmPassword">Confirm Password:</label>
          <Password id="confirmPassword" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} />
        </div>
        <div className="p-field p-col-12 p-md-6">
          <label htmlFor="email">Email:</label>
          <InputText id="email" value={email} onChange={(e) => setEmail(e.target.value)} type="email" />
        </div>
        <div className="p-field p-col-12">
          <Button type="submit" label="Register" className="p-button-primary" />
        </div>
        {error && <ErrorMessage severity="error" text={error} />}
      </form>
    </div>
  );
}

export default Register;
