import { createContext } from "react";

export const defaultLoggedUser = {
  id: "",
  username: "",
  email: "",
};

let loggedUser = defaultLoggedUser;

const setLoggedUser = function(user) {
  loggedUser = user;
};

export const LoggedUserContext = createContext({
  loggedUser,
  setLoggedUser,
});
