import { createContext } from "react";

const defaultLoggedUser = {
  id: "",
  username: "",
  email: "",
};

let loggedUser = defaultLoggedUser;

const setLoggedUser = function(user) {
  alert(user)
  loggedUser = user;
};

const LoggedUserContext = createContext({
  loggedUser,
  setLoggedUser,
});
export {LoggedUserContext,defaultLoggedUser}
