import React from "react";
import { useNavigate } from "react-router-dom";
import styles from "./CstoreMain.module.css";
import { useState,useEffect } from "react";

const CStore = () => {
  const navigate = useNavigate();
 
  const [books,setBooks]=useState(new Array);

  async function showBook() {

    try {
      const response = await fetch("http://localhost:7002/api/books")
      const data = await response.json();
      setBooks(data)
      console.log(books);
    }
    catch (error) {
      console.log("failed to fetch");
    }
  }
  useEffect(() => { showBook() }, [])

  const filters = (page) => {
    navigate(`/${page}` ,{state:books.filter(e=>e.type===page)})
  }
  return (
    <div>
      <div className={styles.containerc}>
        <div className={styles.imagewrapperc}>
          <img
            src="gg.jpg"
            style={{ height: "180px", width: "180px", fontFamily: "initial" }}
            alt="Logo"
          />
        </div>
        <div className={styles.buttonwrapperc}>
          <button className={styles.buttonc} onClick={e=>{filters("teens")}}>
            Teens
          </button>
          <button className={styles.buttonc} onClick={r=>filters("comic")}>
            Comic
          </button>
          <button className={styles.buttonc} onClick={filters("kids")}>
            Kids
          </button>
          <button className={styles.buttonc} onClick={filters("kodesh")}>
            Kodesh
          </button>
        </div>
      </div>
    </div>
  
  );
};

export default CStore;
