import React from "react";

const BookStore = () => {
  return (
    <div
      style={{
        backgroundColor: "black",
        color: "neonGreen",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <h1
        style={{
          fontSize: "48px",
          marginBottom: "20px",
          textShadow: "2px 2px 4px rgba(0,0,0,0.4)",
        }}
      >
        Welcome to the Neon Bookstore!
      </h1>
      <p
        style={{
          fontSize: "24px",
          marginBottom: "40px",
          textShadow: "2px 2px 4px rgba(0,0,0,0.4)",
        }}
      >
        Get lost in the world of neon literature.
      </p>
      <button
        style={{
          padding: "12px 24px",
          fontSize: "18px",
          backgroundColor: "neonGreen",
          color: "black",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
          boxShadow: "2px 2px 4px rgba(0,0,0,0.4)",
        }}
      >
        Explore Books
      </button>
    </div>
  );
};

export default BookStore;
