const BASE_URL = 'https://localhost:44393/api/Authentication'; 

const login = async (username, password) => {
  try {
    const response = await fetch(`${BASE_URL}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
      mode: 'cors', 
    });

    if (!response.ok) {
      throw new Error('Login Failed');
    }

    const data = await response.json();
    onLogin(data.token); 
  } catch (error) {
    setError(error.message);
  }
  try {
    const response = await fetch(`${BASE_URL}/login`, { username, password });
    if (response.data.token) {
      // Login successful, store token (implementation depends on your app)
      localStorage.setItem('userToken', response.data.token);
      return true; // Or return the token itself
    } else {
      throw new Error('Login failed');
    }
  } catch (error) {
    throw error; // Re-throw the error for handling in the component
  }
};

const register = async (username, password, confirmPassword, email) => {
  try {
    const response = await post(`${BASE_URL}/register`, { username, password, confirmPassword, email });
    if (response.status === 201) { // Check for successful creation (201 Created)
      return true; // Or return a success message
    } else {
      throw new Error('Registration failed');
    }
  } catch (error) {
    throw error; // Re-throw the error for handling in the component
  }
};

export { login, register };
