import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Avatar } from 'primereact/avatar';
import { Button } from 'primereact/button';
import { Menu } from 'primereact/menu';
import {MenuItem} from 'primereact/menuitem'
import { Carousel } from 'primereact/carousel';
import { InputText } from 'primereact/inputtext';
import { Dialog } from 'primereact/dialog'; 
import {register} from '../Services/AuthenticationService';
import { login } from '../Services/AuthenticationService';
import './HomePage/HomePage.css';

const HomePage = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();
  const [images, setImages] = useState([
    { src: 'https://localhost:44393/assets/venice.JPG', text: '', style: "carousel-text-1" },
    { src: 'https://localhost:44393/assets/mountain.JPG', text: '', style: "carousel-text-2" },
    { src: 'https://localhost:44393/assets/bay.jpg', text: '', style: "carousel-text-3" },
  ]);
  const menu = useRef(null);

  const [isRegisterDialogVisible, setIsRegisterDialogVisible] = useState(false);
  const [isLoginDialogVisible, setIsLoginDialogVisible] = useState(false);

  const handleRegisterClick = () => {
    setIsRegisterDialogVisible(true);
  };

  const handleLoginClick = () => {
    setIsLoginDialogVisible(true);
  };

  const handleDialogHide = () => {
    setIsRegisterDialogVisible(false);
    setIsLoginDialogVisible(false);
  };
  

  const itemTemplate = (item) => {
    return (
      <div className="image-container">
        <div
          className="carousel-image"
          style={{
            backgroundImage: `url(${item.src})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            opacity: 0.8,
          }}
        >
          <div className={item.style}>
            <h2>{item.text}</h2>
          </div>
        </div>
      </div>
    );
  };

  const profileMenuItems = [
  
    !isLoggedIn && {
      label: 'Sign Up',
      icon: 'pi pi-sign-in',
      onClick: handleRegisterClick,
    },
    {
      label: isLoggedIn ? 'Log Out' : 'Login',
      icon: isLoggedIn ? 'pi pi-sign-out' : 'pi pi-sign-in',
      onClick: handleLoginClick,
    },
  ];

  const LoginDialog = () => (
    <Dialog header="Login" visible={isLoginDialogVisible} onHide={handleDialogHide}>
      <form onSubmit={login}>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" />
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" />
        <button type="submit">Login</button>
      </form>
    </Dialog>
  );

  const RegisterDialog = () => (
    <Dialog header="Register" visible={isRegisterDialogVisible} onHide={handleDialogHide}>
      <form onSubmit={register}>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" />
        <label htmlFor="password">Password:</label>
        <input type="password" id="password" />
        <label htmlFor="confirmPassword">Confirm Password:</label>
        <input type="password" id="confirmPassword" />
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" />
        <button type="submit">Register</button>
      </form>
    </Dialog>
  );


  return (
  <div>
          <LoginDialog />
          <RegisterDialog />
    <div className="menu-bar">
        <img src='https://localhost:44393/assets/logo1.png' alt="Logo" className="menu-logo" />
        <div className="profile-container">
        <div onClick={(event) => menu.current.toggle(event)}>  
        <Avatar icon="pi pi-user" size="large"shape="circle" className="profile-icon"  />
       </div>
        <Menu model={profileMenuItems} popup ref={menu} >
          {isLoggedIn &&<MenuItem  icon='pi pi-star'  >
          Starred Trips
          </MenuItem>}
          {isLoggedIn &&<MenuItem  icon='pi pi-star'  >
          Starred Trips
          </MenuItem>}
          {!isLoggedIn &&<MenuItem  icon='pi pi-sign-in'  >
          Starred Trips
          </MenuItem>}
          {!isLoggedIn &&<MenuItem  icon='pi pi-star'  >
          Starred Trips
          </MenuItem>}
        </Menu>
        </div>     
    </div>

      <div className="carousel-container">
        <Carousel
          value={images}
          numVisible={1}
          numScroll={1}
          itemTemplate={itemTemplate}
          circular
          autoplayInterval={4000}
          className="custom-carousel"
        />
  </div>

    <div className="joinEmail">
      <div className="joinEmail-text">
          <h3>Get On The List 📧</h3>
          <h5>Subscribe to JourneyJOT emails for peoples cool journeys around the globe</h5>
          <h5>can't wait to see you 😎</h5>
      </div> 
      
      <InputText keyfilter="email" placeholder="Enter your email address"  style={{ width: '300px',height:'50px',borderRadius:"40px", padding: '0.5rem',margin: '20px 20px' }} />
      <Button type="submit" label="Join The Fun" style={{ padding: '0.5rem 1rem', backgroundColor: 'black', color: 'white',borderRadius:"5px", }}
              onClick={(e) => {
                  // your function to handle the subscription
              }} />
    </div>

  <div className="footer">
    <p>&copy; 2023 JourneyJOT | All Rights Reserved</p>
    <p>Terms of Service | Privacy Policy</p>
    <p>Contact Us:
    <ul>
      <li><i icon="pi pi-envelope"></i> [info@journeyjot.com](mailto:info@journeyjot.com)</li>
      <li><i className="fa fa-phone"></i> +1 555 555 5555</li>
      <li><i className="fa fa-map-marker"></i> 123 Main St, Anytown, USA 12345</li>
    </ul></p>
  </div>
</div>
    
  );
};

export default HomePage;
