    //  <img src={i.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
    return (
        <>
        <button onClick={()=>updateBook()}>Add New Book</button>
           {books && books.map((i,index) => {
            return <div className='border' key={i._id}>
                 
                   <div>{i.name + " | " + i.price + "₪"}</div>
                   <button key={index} onClick={()=>updateBook(i)}>Edit</button>
                   <dialog id="dialog">
                    <h1>dsfzx</h1>
                    <input text={i.name}></input>
                    <input text={i.price}></input>
                    <textarea placeholder={i.author}></textarea>
                    <image></image>
                    <button onClick={()=>update()}>save</button>
                  
                   </dialog>
                   <button onClick={()=>deleteBook(i)}>Remove</button>        
            </div>})} 
           
        </>);
    

}
export default Store;
