import { useState,useEffect } from "react";

const Store = ()=>{
    const [books, setBooks] = useState([]);
    const [a, setA] = useState("");
    const [b, setB] = useState("");
    const [c, setC] = useState(0);
    const [d, setD] = useState("");
    async function showBook() {
        try {
          const response = await fetch("http://localhost:7002/api/books")
          const data = await response.json();
          setBooks(data)
          console.log(books);
        }
        catch (error) {
          console.log("failed to fetch");
        }
      }
      useEffect(() => { showBook() }, [])


    const deleteBook=(b)=>{
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          code: b._id
        });
        
        var requestOptions = {
          method: 'DELETE',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        
        fetch("http://localhost:7002/api/books", requestOptions)
          .then(response => response.text())
          .then(result => alert(result))
          .catch(error => console.log('error', error));
    }
    const updateBook=()=>{
       // debugger
      //  var x = document.createElement("DIALOG");
        //document.getElementById("dialog").showModal();

    }
    const update=async ()=>{
 
        //debugger
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          "name": a,
          "code": b,
          "price": c,
          "author":d
        });
        //debugger
        var requestOptions = {
          method: 'PUT',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        debugger
         fetch("http://localhost:7002/api/books", requestOptions)
          .then(response => response.text())
          .then(result => console.log(result))
          .catch(error => console.log('error', error));
          await document.getElementById("dialog").close();
          await showBook()
        }  

    //  <img src={i.image} style={{ heignt: "150px", width: "200px", fontFamily: "initial" }}></img>
    return (
        <>
        <button onClick={()=>updateBook()}>Add New Book</button>
           {books && books.map((i,index) => {
            return <div className='border' key={i._id}>
                 
                   <div>{i.name + " | " + i.price + "₪"}</div>
                   <button key={index} onClick={()=>updateBook(i)}>Edit</button>
                   <dialog id="dialog">
                    <h1>Edit Book</h1>
                    <input defaultValue={i.name} onChange={e=>setA(e.target.value)}></input>
                
                    <br></br>
                    <input defaultValue={i.code} id="b" onChange={e=>setB(e.target.value)}></input>
                    <br></br>
                    <input defaultValue={i.price} id="c"  onChange={e=>setC(e.target.value)}></input>
                    <br></br>
                    <textarea defaultValue={i.author} id="d"></textarea>
                    <br></br>
                    <img></img>
                    <br></br>
                    <button onClick={()=>update()}>save</button>
                   </dialog>
                   <button onClick={()=>deleteBook(i)}>Remove</button>        
            </div>})} 
           
        </>);
    

}
export default Store;
