import React, { useEffect, useState } from 'react';
import { fetchEntries } from '../TripService';
import { Carousel } from 'primereact/carousel';
import { Card } from 'primereact/card';
import { Dialog } from 'primereact/dialog';

const TripsView = () => {
  const [entries, setEntries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [visible, setVisible] = useState(false);
  const [selectedEntry, setSelectedEntry] = useState(null);

  useEffect(() => {
    fetchEntries()
      .then(data => {
        const sortedEntries = data.sort((a, b) => new Date(a.startDate) - new Date(b.startDate));
        setEntries(sortedEntries);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching entries:', error);
        setError(error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  const getCardClassName = (startDate, endDate) => {
    const now = new Date();
    const start = new Date(startDate);
    const end = new Date(endDate);

    if (start > now) {
      return 'future-trip';
    } else if (end < now) {
      return 'past-trip';
    } else {
      return 'current-trip';
    }
  };

  const cardTemplate = (entry) => {
    const cardClassName = getCardClassName(entry.startDate, entry.endDate);
    const cardStyles = {
      width: '16rem',
      height: '16rem',
      border: cardClassName === 'current-trip' ? '4px solid orange' :
              cardClassName === 'past-trip' ? '4px solid red' :
              '4px solid green',
      cursor: 'pointer',
      backgroundImage: `url('https://localhost:44393/${entry.coverPhotoUrl}')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      color: 'white',
      position: 'relative',
    };

    const headerStyles = {
      position: 'absolute',
      top: '0',
      left: '0',
      width: '100%',
      backgroundColor: cardClassName === 'current-trip' ? 'orange' :
                       cardClassName === 'past-trip' ? 'red' :
                       'green',
      padding: '0.5rem',
      fontSize: '1rem',
      fontWeight: 'bold',
      color: 'white',
      zIndex: '1',
    };

    const handleClick = () => {
      setSelectedEntry(entry);
      setVisible(true);
    };

    return (
      <Card className="trip-card" style={cardStyles} onClick={handleClick}>
        <div style={headerStyles}>
          {entry.title}
          <p style={{ color: 'white', fontSize: '0.8rem', marginTop: '0.5rem' }}>
            {new Date(entry.startDate).toLocaleDateString()} - {new Date(entry.endDate).toLocaleDateString()}
          </p>
        </div>
        <div className="p-card-content">
          {/* Optionally add more content below the header */}
        </div>
      </Card>
    );
  };

  const onHide = () => {
    setVisible(false);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100vh' }}>
      <Carousel value={entries} itemTemplate={cardTemplate} numVisible={3} numScroll={1} responsiveOptions={[
        {
          breakpoint: '1024px',
          numVisible: 2,
          numScroll: 1,
        },
        {
          breakpoint: '768px',
          numVisible: 1,
          numScroll: 1,
        }
      ]} />

      <Dialog visible={visible} onHide={onHide}>
        {selectedEntry && (
          <>
            <h3>{selectedEntry.title}</h3>
            <p>{selectedEntry.description}</p>
          </>
        )}
      </Dialog>
    </div>
  );
};

export default TripsView;
