import React, { useEffect, useState } from 'react';
import { fetchJournalEntrysByTripId } from '../Services/JournalEntryService';
import { Sidebar } from 'primereact/sidebar';
import { Button } from 'primereact/button';
import { DataView } from 'primereact/dataview';
import {Skeleton} from 'primereact/skeleton'
import { Tag } from 'primereact/tag';
import {Rating} from 'primereact/rating'
import { Dialog } from 'primereact/dialog';
import { Dropdown } from 'primereact/dropdown';
import { IconField } from 'primereact/iconfield';
import { InputIcon } from 'primereact/inputicon';
import { InputText } from 'primereact/inputtext';
import MenuBar from './MenuBar';
import './style sheets/TripsView.css'
import { useNavigate } from 'react-router-dom';

const JournalView = () => {
    const [journalEntries, setJournalEntries] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedEntry, setSelectedEntry] = useState(null);
    const [visible, setVisible] = useState(true);
    const navigate=useNavigate()
    useEffect(() => {
        fetchJournalEntrysByTripId()
        .then((data) => {
          const sortedEntries = data.sort((a, b) => new Date(a.date));
          setJournalEntries(sortedEntries);
          setLoading(false);
        })
        .catch((error) => {
          console.error('Error fetching entries:', error);
          setError(error);
          setLoading(false);
        });
    }, []);
    const entryTemplate = (entry) => {
        // const tripStatus = getCardClassName(trip.startDate, trip.endDate);
        return (
          <div className="col-12 sm:col-6 lg:col-12 xl:col-4 p-2 " key={entry.id} style={{ backgroundColor:"#ADD8E6"}}>
            <div className="p-4 border-1 surface-border surface-card border-round" style={{ maxWidth: '30rem',  maxHeight: '25rem' }}>
              <div className="flex flex-wrap align-items-center justify-content-between gap-2">
                <div className="flex align-items-center gap-2">
                  <i className="pi pi-calendar"></i>
                  <span className="font-semibold">{new Date(entry.date).toLocaleDateString('en-US', { day: 'numeric', month: 'short', year: 'numeric' })}</span>
                </div>
                {/* <Tag value={trip.status} severity={getSeverityByStatus(tripStatus)}>{tripStatus==='future-trip'? "Future Trip" :tripStatus==='happening now'? "Trip Happening Now" :tripStatus==='past-trip'?"Past Trip" : ""}</Tag> */}
              </div>
              <div className="flex flex-column align-items-center gap-3 py-5">
              <img
              className="w-full h-9 shadow-2 border-round"
              src={`https://localhost:44393/${trip.coverPhotoUrl}`}
              alt={entry.text}
              style={{ maxHeight: '150px', objectFit: 'cover' }}
            />
                <div className="text-2xl font-bold">{entry.title}</div>
              </div>
              <div className="flex align-items-center justify-content-center">
                {trip.status !== 'future-trip' && (
                  <Button
                    icon="pi pi-eye"
                    className="p-button-rounded"
                    onClick={() =>{
                      setSelectedEntry(trip);
                      }}
                  >
                    View Journal
                  </Button>
                )}
              </div>
            </div>
          </div>
        );
      };

  return (
    <div className="card flex justify-content-center">
    <Sidebar visible={visible} onHide={() => setVisible(false)}>
        <h2>Entries</h2>
        <DataView 
        value={journalEntries} 
        itemTemplate={tripTemplate} 
        layout={'grid'} 
        cols={4} 
        className="dataView"
        />
    </Sidebar>
    {/* <Button icon="pi pi-arrow-right" onClick={() => setVisible(true)} /> */}
</div>
  );
};

export default JournalView;
