const loginService = (user) => {
  debugger
  var myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");
  
  var raw = JSON.stringify({
    "userName": user.userName,
    "password": user.password
  });
  
  var requestOptions = {
    method: 'POST',
    headers: myHeaders,
    body: raw,
    redirect: 'follow'
  };
  
  return new Promise((resolve, reject) => {
    fetch("http://localhost:7002/api/auths/login", requestOptions)
      .then(response => {
        if (!response.ok) {
         console.log("response error"+response) // new Error("unknown");
        }
        return response.text();
      })
      .then(result => {
        resolve({
          loginStatus: "ok",
          data: { id: user.password, name: user.userName, userToken: result },
        });
      })

  });
};


