import { useState,useEffect } from "react";



const CStore = ()=>{
    const [books, setBooks] = useState([]);
    async function showBook() {
      try {
        const response = await fetch("http://localhost:7002/api/books")
        const data = await response.json();
        setBooks(data)
        console.log(books);
      }
      catch (error) {
        console.log("failed to fetch");
      }
    }
    useEffect(() => { showBook() }, [])
    const addToCart=(b)=>{
        var myHeaders = new Headers();
        myHeaders.append("Authorization", "Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2NGIzOTU3YWZjYWVmMjIyYjY1OWY5NDkiLCJuYW1lIjoibmQiLCJyb2xlcyI6InVzZXIiLCJ1c2VyTmFtZSI6Im4iLCJlbWFpbCI6InVodiIsImlhdCI6MTY4OTQ5NjQ3OH0.upOC0CH22-aDPE6L7ArMM3S0OTROJc4RKHBGCarg6WQ");
        myHeaders.append("Content-Type", "application/json");
        
        var raw = JSON.stringify({
          "book": "64aea21ee04dd81b5fd416a1"
        });
        
        var requestOptions = {
          method: 'POST',
          headers: myHeaders,
          body: raw,
          redirect: 'follow'
        };
        
        fetch("http://localhost:7002/api/carts", requestOptions)
          .then(response => response.text())
          .then(result => console.log(result))
          .catch(error => console.log('error', error));
    }
    
