        <>
        {books.map((b)=>
          <div key={b._id}>
          {b.name + b.price+"â‚ª"}
          <button onClick={() => addToCart(b)}>AddðŸ›’</button>
          </div>  
       )} 

        </>
    );

}
export default CStore;
